mapper.select:
select no, title, writer, content, state, datetime
  from answer 
 where no = #{no}

mapper.insert:
insert into answer(
	title, writer, content, state
) values (
	#{title}, #{writer}, #{content}
)

mapper.update:
update answer 
   set content = #{content}
   	   state = #{state}
 where no = #{no}
